
var mySwiper = new Swiper('.swiper-container', {
    autoplay: false,
    navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
    },
});


window.addEventListener('scroll', function() {

    var trigger1 = document.getElementById('trigger_800').offsetTop;
    var trigger2 = document.getElementById('trigger_1600').offsetTop;
    
    if (window.pageYOffset + 800 > trigger1) {
        mySwiper.autoplay.stop();
    }
    if (window.pageYOffset + 800 > trigger2) {
    }

});